mapper.update:
UPDATE Bg_Interest_category
SET category_name = #{categoryName},
    parent_id = #{parentId},
    depth = #{depth}
WHERE id = #{id}

mapper.select:
WITH RECURSIVE category_tree AS ( -- 시작 노드 (선택한 카테고리) SELECT id, parent_id, depth FROM Bg_Interest_category WHERE id = #{categoryId}
UNION ALL
-- 하위 카테고리들 SELECT c.id, c.parent_id, c.depth FROM Bg_Interest_category c INNER JOIN category_tree ct ON c.parent_id = ct.id ) SELECT id FROM category_tree WHERE id != #{categoryId} -- 자기 자신은 제외 (하위 카테고리만)
