mapper.insert:
WITH updated AS (
    UPDATE Bg_Cart
    SET quantity = quantity + #{quantity} 
    WHERE user_id = #{userId}
    AND product_id = #{productId}
    RETURNING 1
)
INSERT INTO Bg_Cart (user_id, product_id, quantity)
SELECT #{userId}, #{productId}, #{quantity}
WHERE NOT EXISTS (SELECT 1 FROM updated)
