mapper.select:
SELECT DISTINCT p.id AS productId, p.product_name AS productName, p.origin_name AS originName, p.content, p.price, p.image_url AS imageUrl, c.id AS cat_id, c.category_name AS cat_categoryName, c.parent_id AS cat_parentId, c.depth AS cat_depth, CASE WHEN #{userId} IS NULL THEN 0 WHEN w.user_id IS NOT NULL THEN 1 ELSE 0 END AS isWished, COALESCE((SELECT AVG(rating) FROM Bg_Review WHERE product_id = p.id), 0) AS averageRating, COALESCE((SELECT COUNT(*) FROM Bg_Review WHERE product_id = p.id), 0) AS reviewCount FROM Bg_Product p LEFT JOIN Bg_Category_product_mapping cpm ON p.id = cpm.product_id LEFT JOIN Bg_Interest_category c ON cpm.interest_category_id = c.id LEFT JOIN Bg_Wishlist w ON p.id = w.product_id
AND w.user_id = #{userId}
AND 1=0
AND (p.product_name LIKE '%' || #{keyword} || '%' OR p.content LIKE '%' || #{keyword} || '%' OR p.origin_name LIKE '%' || #{keyword} || '%')
AND cpm.interest_category_id IN
#{id}
AND cpm.interest_category_id = #{categoryId}
ORDER BY p.product_name ASC DESC
ORDER BY p.price ASC DESC
ORDER BY p.id ASC DESC
ORDER BY p.id DESC
LIMIT #{size} OFFSET #{offset}
